// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract RIFCounter {
  uint256 public count = 0;

  event Counted(uint256 count);
  // slither-disable-next-line locked-ether

  function inc() external payable {
    count += count + 1;
    emit Counted(count);
  }

  function fail() external pure {
    revert('Boom');
  }
}
